import './TodoListContainer.css';
import { TodoCounter } from '../TodoCounter';
import { TodoList } from '../TodoLIst';
import { TodoSearch } from '../TodoSearch';
import { TodoItem } from '../TodoItem';
import React from 'react';


function TodoListContainer ({todos, setTodos, completedTodos, totalTodos, searchValue, setSearchValue, deleteTodos,loading,error, markTodoAsCompleted}) {

    let recivedTodos = todos;

    console.log(loading);
    return (
        <section className='TodoListContainer'>
            <h1>Your Tasks</h1>
            <TodoCounter 
            completed={completedTodos}
            total={totalTodos}/>
            <TodoSearch 
            searchValue={searchValue} 
            setSearchValue={setSearchValue}/>
             <TodoList>

                {loading && <p>Cargando...</p>}
                {error && <p>Hubo un error</p>}
                {(!loading && recivedTodos.length === 0)&& <p>No hay tareas</p>}
                {recivedTodos.map(todo => (
                    <TodoItem 
                    key={todo.text}
                    text={todo.text}
                    completed={todo.completed}
                    onCompleted ={() => markTodoAsCompleted(todo.text)}
                    onDelete={() => deleteTodos(todo.text)}
                    />
                ))}

            </TodoList>


        </section>

    );
}

export {TodoListContainer};
