import './App.css';
import React from 'react';
import { TodoCreatorCountainer } from './TodoCreatorCountainer';
import { TodoListContainer } from './TodoListContainer';


function useLocalStorage(itemName, initialValue) {
  const localStorageItem = JSON.parse(localStorage.getItem(itemName));
  let parsedItem;
  if(!localStorageTodos){
    localStorage.setItem(itemName, JSON.stringify(initialValue));
    parsedTodos = initialValue;
  } else {
   parsedTodos = JSON.parse(localStorage.getItem(itemName));
  }
  const [item, setItem] = React.useState(parsedItem);
  const saveItem = (newItem) =>{
    localStorage.setItem(itemName, JSON.stringify(newItem));
    setItem(newItem);
  };
  return [saveItem, item];
}
function App() {
const [todos,setTodos] = React.useState ( parsedTodos );
const  completedTodos = todos.filter(todo => todo.completed).length
const  totalTodos = todos.length
const [searchValue, setSearchValue] = React.useState('');
const filterTodos = todos.filter(todo => { return todo.text.toLowerCase().includes(searchValue.toLowerCase())});
const [newTodo, setNewTodo] = React.useState({ text: 'primer', completed: false });
const addTodos = (todo) =>{
  console.log(todo);
  setTodos([...todos, todo]);
  localStorage.setItem('TODOS_V1', JSON.stringify([...todos, todo]));
};
const deleteTodos = (todo) =>{
console.log("DeleteTodos");
console.log(todo);
setTodos(todos.filter(t => t.text !== todo));
localStorage.setItem('TODOS_V1', JSON.stringify(todos.filter(t => t.text !== todo)));

}
  return (
    <div className='App'>
      <TodoCreatorCountainer
      setNewTodo = {setNewTodo}
      addNewTodos = {() =>addTodos(newTodo)}/>
      <TodoListContainer
       todos = {filterTodos}
       setTodos={setTodos}
       completedTodos={completedTodos} 
       totalTodos={totalTodos} 
       searchValue={searchValue} 
       setSearchValue={setSearchValue}
       deleteTodos={deleteTodos}
       />
    </div>
  );
}
export default App;
