import './App.css';
import React from 'react';
import { useLocalStorage } from './useLocalStorage';
import { AppUI } from './AppUI';



function App() {
const [todos,saveTodos] = useLocalStorage('TODOS_V1', []);
const  completedTodos = todos.filter(todo => todo.completed).length
const  totalTodos = todos.length
const [searchValue, setSearchValue] = React.useState('');
const filterTodos = todos.filter(todo => { return todo.text.toLowerCase().includes(searchValue.toLowerCase())});
const [newTodo, setNewTodo] = React.useState({ text: 'primer', completed: false });
const addTodos = () =>{
  saveTodos([...todos]);
};
const deleteTodos = (todo) =>{
console.log("DeleteTodos");
console.log(todo);
saveTodos(todos.filter(t => t.text !== todo));

}
  return (
    <AppUI
    todos = {todos}
    setTodos={saveTodos}
    filterTodos={filterTodos}
    completedTodos={completedTodos}
    totalTodos={totalTodos}
    searchValue={searchValue}
    setSearchValue={setSearchValue}
    deleteTodos={deleteTodos}
    addTodos={addTodos}
    setNewTodo={setNewTodo}
    />
  );
}
export default App;
